import { useState, useEffect } from "react";
import { Navbar, Container, Nav } from "react-bootstrap";
import logo from "../assets/img/logo.svg";
import navIcon1 from "../assets/img/nav-icon1.svg";
import navIcon2 from "../assets/img/nav-icon2.svg";
import navIcon3 from "../assets/img/nav-icon3.svg";

export const NavBar = () => {
    const[activeLink, setActiveLink] = useState('home');
    const[scrolled, seScrolled] = useState(false);

    useEffect(() => {
        const onScroll = () => {
            if (window.scrollY > 50) {
                seScrolled(true);
            } else {
                seScrolled(false);
            }
        }

        window.addEventListener("scroll", onScroll);

        return () => window.removeEventListener("scroll", onScroll);
    })

    const onUpdateActiveLink = (value) => {
        setActiveLink(value);
    }

    return(
            <Navbar expand='lg' className={scrolled ? "scrolled": ""} >
                <Container>
                    <Navbar.Brand href="#home">
                        <img src={logo} alt="Logo" />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav">
                        <span className="navbar-toggler-icon"></span>
                    </Navbar.Toggle>
                    <Navbar.Collapse id ="basic-navbar-nav">
                        <Nav className="me-auto"> 
                            <Nav.Link href="#home" className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('home')}>Home</Nav.Link>
                            <Nav.Link href="#skills" className={activeLink === 'skills' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('skills')}>Skills</Nav.Link>
                            <Nav.Link href="#project" className={activeLink === 'projects' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('projects')}>Projects</Nav.Link>
                        </Nav>
                        <span className="navbar-text">
                            <div className="social-icon">
                                <a href='https://github.com/victor-oliveira-dev98' target="_blank"> <img src={navIcon1} alt=""/></a>
                                <a href='https://www.linkedin.com/in/victor-hugo-faria-de-oliveira-340b71190/' target="_blank" rel="noreferrer"> <img src={navIcon2} alt=""/></a>
                                <a href='https://www.instagram.com/victor_h.fo/' target="_blank"> <img src={navIcon3} alt=""/></a>
                            </div>
                            <a className="text-decoration-none" href="https://api.whatsapp.com/send?phone=353834489081" target="_blank"> <button className="vvd" onClick={() => console.log('connect')}><span>Let's Connect</span></button></a> 
                        </span> 
                    </Navbar.Collapse>

                </Container>

            </Navbar>
    )
}
